public class Pattern_0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		Scanner Sc = new Scanner( System.in);

        int n = Sc.nextInt();
       
        int i=1;
       
        while(i <= n) {
    	   
    	   System.out.print("*");
    	   
    	   i++;
    	   
        }
       }
     }
